import React from "react";
import logo from "../../images/logo.svg";
import ExitHeader from "../ExitHeader/ExitHeader";
import "../Header/header.css";
import Navigation from "../Navigation/Navigation";
import {Link, NavLink} from "react-router-dom";
import icon from "../../images/icon__user.svg";
//{ <Navigation/> }
function Header({ isLoggedIn }) {
    return (
        <header className={`header ${isLoggedIn ? "header_theme_light" : ""}`}>
            <Link to="/">
                <img className="header__logo" src={logo} alt="Логотип" />
            </Link>
            <NavLink to="/profile" className="navigation__profile">
                Аккаунт
                <img className="navigation__profile-img" src={icon} alt="Аккаунт"/>
            </NavLink>
            {isLoggedIn ? <Navigation /> : <ExitHeader />}
        </header>
    )
}

export default Header;
