import React from "react";
import logo from "../../images/logo.svg";
import ExitHeader from "../ExitHeader/ExitHeader";
import "../Header/header.css";
import Navigation from "../Navigation/Navigation";
import { Link } from "react-router-dom";

function Header({ isLoggedIn }) {
    const [isNavigationMenuOpen, setIsNavigationMenuOpen] = React.useState(false);

    function toggleButtonMenu() {
        setIsNavigationMenuOpen(!isNavigationMenuOpen)
    }

    return (
        <header className={`header ${isLoggedIn ? "header_theme_light" : "header"}`}>
            <Link to="/">
                <img className="header__logo" src={logo} alt="Логотип" />
            </Link>
            <Navigation isNavigationMenuOpen={setIsNavigationMenuOpen} />
        </header>
    )
    /*return (
        // <header className='header header_theme_light'>
        <header className={`header ${isLoggedIn ? "header_theme_light" : "header"}`}>
            <Link to="/">
                <img className="header__logo" src={logo} alt="Логотип" />
            </Link>
            { <Navigation/> }
            {isLoggedIn ? <Navigation /> : <ExitHeader />}
        </header>
    )*/
}

export default Header;
