import React from "react";
import "../SeacrhForm/seacrhForm.css";
import useFormValidation from '../../hooks/UseFormValidation';
import validator from "validator";

function SearchForm({
                      handleSearchSubmit = () => {},
                      shortMovies = false,
                      checkBoxClick = () => {},
                      inputValue
                    }) {
  const [inputSearch, setInputSearch] = React.useState("");
  const [message, setMessage] = React.useState('');
  /*   const [values, setValues] = React.useState({});
    const [isValid, setIsValid] = React.useState(false);
    const [errors, setErrors] = React.useState({});

    function handleChange(e) {
        if (e.target.name === "film") {
            if (!validator.isDate(e.target.value)) {
                e.target.setCustomValidity("Нужно ввести ключевое слово");
            } else {
                e.target.setCustomValidity("");
            }
        }
        setValues({ ...values, [e.target.name]: e.target.value });
        setIsValid(e.target.closest("form").checkValidity());
        setErrors({ ...errors, [e.target.name]: e.target.validationMessage });

    }
 */
    function handleChangeInput(e) {
    setInputSearch(e.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    if(message.trim().length !== 0){
        console.log('sss')
    } else {
        handleSearchSubmit(inputSearch);
    }
    //handleSearchSubmit(inputSearch);
  }

  React.useEffect(() => {
    setInputSearch(inputValue);
  }, [inputValue]);
  return (
      <form className="searchForm" onSubmit={handleSubmit}>
        <div className="searchForm__main-string">
            <div className="searchForm__container">
                <input
                    className="searchForm__input"
                    onChange={handleChangeInput}
                    name="film"
                    value={inputSearch || ""}
                    type="text"
                    placeholder="Фильм"
                    required
                ></input>
                <span className="searchForm__error round">{message}</span>
            </div>
          <button type="submit" className="searchForm__button">
            Найти
          </button>
        </div>

        <div className="searchForm__switch-line">
          <label className="searchForm__switch">
            <input
                type="checkbox"
                className="searchForm__switch-input"
                checked={shortMovies}
                onChange={checkBoxClick}
            />
            <span className="searchForm__switch-slider round"></span>
          </label>
          <p className="searchForm__switch-text">Короткометражки</p>
        </div>
      </form>
  );
}

export default SearchForm;
