import { Routes, Route, useLocation } from "react-router-dom";
import Login from "../Login/Login.js";
import Register from "../Register/Register.js";
import NotFound from "../NotFound/NotFound.js";

function App() {
    const { pathname } = useLocation();

    return (
        <div className="App">

            {pathname === "/"}
            <Routes>
                <Route path="/signup"
                       element={<Register />}>
                </Route>
                <Route path="/signin"
                       element={<Login />}>
                </Route>
                <Route path="*"
                       element={<NotFound />}>
                </Route>
            </Routes>
            {pathname === "/"}
        </div>
    )
}

export default App;
