import React from "react";
import logo from "../../images/logo.svg";
import ExitHeader from "../ExitHeader/ExitHeader";
import Navigation from "../Navigation/Navigation";
import "../Header/header.css";

import {Link, Route} from "react-router-dom";
//для просмотра хедера авторизованного пользователя расскоментируйте 11 и 16 строку, а 12 и 17 наоброт закомментируйте
function Header({ isLoggedIn }) {
    return (
        <switch>
            <Route exact path="/">
                <header className={`header ${isLoggedIn ? "header_theme_light" : ""}`}>
                    <Link to="/">
                        <img className="header__logo" src={logo} alt="Логотип" />
                    </Link>
                    <Navigation isLoggedIn={isLoggedIn} isHeaderColored={true} />
                </header>
            </Route>
            <Route path="/(movies|saved-movies|profile)">
                <header className="header header_theme_light">
                        <Link to="/">
                            <img src={logo} alt="Логотип сайта" />
                        </Link>
                        <Navigation isLoggedIn={isLoggedIn} isHeaderColored={false} />
                </header>
            </Route>

        </switch>
        // <header className='header header_theme_light'>

    )
}

export default Header;
